import random

def generate_series():
    soil_moisture = np.zeros(365)
    for i in range(365):
        rain = random.random() < 0.3
        if rain:
            soil_moisture[i] = min(soil_moisture[i - 1] + np.random.normal(3, 1), 100)
        else:
            soil_moisture[i] = max(soil_moisture[i - 1] - np.random.normal(1, 0.5), 0)
    return soil_moisture
