import numpy as np
import random

def generate_series():
    base_sales = 50
    hourly_sales = [base_sales + np.random.normal(0, 5) for _ in range(24 * 14)]
    
    for i in range(len(hourly_sales)):
        day_of_week = (i // 24) % 7
        hour = i % 24
        
        # Weekend effect
        if day_of_week in [5, 6]:
            hourly_sales[i] += random.uniform(5, 15)
        
        # Evening effect
        if 18 <= hour <= 22:
            hourly_sales[i] += random.uniform(10, 20)
        
        # Sports event effect
        if 8 <= ((i // 24) % 14) <= 21:
            hourly_sales[i] += random.uniform(20, 35)
            
    return np.array(hourly_sales)
