import numpy as np
import random

def general_trend(x):
    return 0.8 + 0.08 * x

def weekly_pattern(x):
    return 2 * np.sin(2 * np.pi * x / 7)

def discount_end_rush(x):
    return 6 * (1 - np.exp(-0.1 * (x - 59)))

def external_factors(x):
    return random.gauss(0, 0.5)

def generate_series():
    x = np.arange(0, 60)
    y = general_trend(x) + weekly_pattern(x) + discount_end_rush(x)
    y += np.vectorize(external_factors)(x)
    y = np.maximum(y, 0)
    return y
