import numpy as np

def generate_series():
    np.random.seed(0)
    baseline = np.random.normal(loc=500, scale=100, size=336)
    maintenance_effect = np.zeros(336)
    for i in range(14): 
        maintenance_effect[i*24 + 11] = -400
    recovery_effect = np.zeros(336)
    for i in range(14): 
        for j in range(12): 
            if i*24 + 11 + j < 336: 
                recovery_effect[i*24 + 11 + j] = j * 33.3
    return np.maximum(0, baseline + maintenance_effect + recovery_effect)
